  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `run_version` varchar(64) DEFAULT NULL COMMENT '运行版本',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

